var socket=io();socket.on("selectednum",function(a){console.log("selectednum: "+a);$("#num"+a).removeClass("unanswered");$("#num"+a).addClass("selected")});socket.on("unanswered",function(a){console.log("unanswered: "+a);$("#num"+a).removeClass("selected");$("#num"+a).addClass("unanswered")});socket.on("correct",function(a){console.log("correct: "+a);$("#num"+a).removeClass("selected");$("#num"+a).addClass("correct")});socket.on("wrong",function(a){console.log("wrong: "+a);$("#num"+a).removeClass("selected");$("#num"+a).addClass("wrong")});